    private void jbContinuarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbContinuarActionPerformed
        int controleVisitante = JFPrincipal.VISITAS.buscarRgVisitante(this.jtfRg.getText().trim());
        int controleAssociado = JFPrincipal.VISITAS.buscarRgAssociado(this.jtfRg.getText().trim());
        boolean associado;
        
        if(controleAssociado == -1)
            associado = false;
        else
            associado = true;
        
        if(controleVisitante == -1){
            new JFAdicionarVisitaNovoVisitante().setVisible(true, this.jtfRg.getText().trim(), associado);
        }else
            new JFAdicionarVisitaVelhoVisitante().setVisible(true, this.jtfRg.getText().trim(), associado);
    }//GEN-LAST:event_jbContinuarActionPerformed

