    public void setVisible(boolean b, Jaula j){
        this.j = j;
        this.txtId.setText(j.getId()+"");
        this.txtTamanho.setText(String.format("%.0f", j.getTamanho()));
        this.txtCusto.setText(String.format("R$%.2f", j.getCustoMensal()).replace(".", ","));
        this.cmbTipo.setSelectedItem(j.getTipo().toString());
        this.setVisible(b);
    } 
    
