    private void btnSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalvarActionPerformed
        String tamanho = txtTamanho.getText().replace(",", ".");
        String custo = txtCusto.getText().replace(",", ".").replace("R$", "");
        String tipo = cmbTipo.getSelectedItem().toString();
        double t, c;
        
        try{
            t = Double.parseDouble(tamanho);
        }catch(NumberFormatException e){
            JOptionPane.showMessageDialog(rootPane, "Campo tamanho foi preenchido incorretamente!");
            txtTamanho.selectAll();
            txtTamanho.requestFocus();
            return;
        }
        
        try{
            c = Double.parseDouble(custo);
        }catch(NumberFormatException e){
            JOptionPane.showMessageDialog(rootPane, "Campo custo foi preenchido incorretamente!");
            txtTamanho.selectAll();
            txtTamanho.requestFocus();
            return;
        }
        
        j.setCustoMensal(c);
        j.setTamanho(t);
        j.setTipo(tipo.charAt(0) == 'T' ? Tipo.TERRESTRE : Tipo.ANFIBIO);
        
        this.dispose();
    }//GEN-LAST:event_btnSalvarActionPerformed

